async function Pinterest(query){
	return new Promise(async(resolve,reject) => {
  axios.get('https://id.pinterest.com/search/pins/?autologin=true&q=' + query, {
    headers: {
      "cookie" : "_auth=1; _b=\"AVna7S1p7l1C5I9u0+nR3YzijpvXOPc6d09SyCzO+DcwpersQH36SmGiYfymBKhZcGg=\"; _pinterest_; _ir=0"
		}
	}).then(({ data }) => {
	  const x = cheerio.load(data)
		const result = [];
		const _output = [];
    x('div > a').get().map(z => {
      const _url = x(z).find('img').attr('src')
      result.push(_url)
		});
    result.forEach(y => {
		  if(y == undefined) return
		  _output.push(y.replace(/236/g,'736'))
	  })
		_output.shift();
		resolve(_output)
		})
	})
}

